import React, { useState } from 'react';
const Track = () => {
  const [orderId, setOrderId] = useState('');
  const [notification, setNotification] = useState('');
  const [showImage, setShowImage] = useState(false);

  const trackOrder = () => {
    setNotification(`Tracking Order ID: ${orderId}`);
    setShowImage(true); // Show the image once the order is being tracked
  };

  return (
    <main>
      <section id="track-order">
        <h2>Track Your Order</h2>
        <label htmlFor="order-id">Enter your Order ID:</label>
        <input
          type="text"
          id="order-id"
          value={orderId}
          onChange={(e) => setOrderId(e.target.value)}
          placeholder="Order ID"
        />
        <button onClick={trackOrder}>Track</button>

        {notification && <div className="notification">{notification}</div>}

        {/* Display the image based on the "showImage" state */}
        {showImage && (
          <div id="order-image" style={{ marginTop: '20px' }}>
            <img
              src="https://onefivenine.com/images/GoogleMapImages/16_3180_43.jpg" // Replace this URL with the actual image URL
              alt="Order Tracking Visual"
              style={{ width: '69%', height: 'auto' }}
            />
          </div>
        )}
      </section>
    </main>
  );
};

export default Track;
