import React, { useState } from 'react';
import './styles.css';

const Menu = () => {
  // Array of menu items with names, descriptions, image URLs, and sub-items with prices
  const menuItems = [
    { 
      id: 1, 
      name: 'Hot Biryani', 
      description: 'Spicy and flavorful rice with Toppings.', 
      image: 'https://i0.wp.com/blendofspicesbysara.com/wp-content/uploads/2020/10/PXL_20201011_200951855.PORTRAIT-01.jpeg?resize=800%2C840&ssl=1',
      subItems: [
        { id: 'b1', name: 'Paneer Biriyani', price: 150 },
        { id: 'b2', name: 'Mushroom Biriyani', price: 120 },
        { id: 'b3', name: 'Chicken Biriyani', price: 200 },
        { id: 'b4', name: 'Fried Rice', price: 100 },
        { id: 'b5', name: 'Veg Biriyani', price: 130 },
        { id: 'b6', name: 'Shahi Biriyani', price: 180 },
      ]
    },
    { 
      id: 2, 
      name: 'Spicy Curries', 
      description: 'Creamy and Spiciest rich gravy.', 
      image: 'https://www.cookwithmanali.com/wp-content/uploads/2019/05/Paneer-Butter-Masala-500x500.jpg',
      subItems: [
        { id: 'c1', name: 'Paneer Butter Masala', price: 200 },
        { id: 'c2', name: 'Palak Paneer', price: 180 },
        { id: 'c3', name: 'Kaju Paneer', price: 220 },
        { id: 'c4', name: 'Mix Veg', price: 150 },
        { id: 'c5', name: 'Shahi Paneer', price: 250 },
        { id: 'c6', name: 'Mushroom Veg Mix', price: 180 },
        { id: 'c7', name: 'Chicken Squash', price: 300 },
        { id: 'c8', name: 'Egg Mix', price: 120 },
      ]
    },
    { 
      id: 8, 
      name: 'Beverages', 
      description: 'Chilled Drinks.', 
      image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRXbtx6UV9QXgp2tv4JLJkCy1QiW92vebChOw&s',
      subItems: [
        { id: 'd1', name: 'Coca-Cola', price: 40 },
        { id: 'd2', name: 'Sprite', price: 35 },
        { id: 'd3', name: 'Pepsi', price: 40 },
        { id: 'd4', name: 'Mountain Dew', price: 35 },
        { id: 'd5', name: 'Maaza', price: 30 },
        { id: 'd6', name: 'Mirinda', price: 35 },
        { id: 'd7', name: 'Limca', price: 35 },
      ]
    },
    // Add other items as needed...
  ];

  const [selectedItem, setSelectedItem] = useState(null); // Track selected category
  const [subItemCounts, setSubItemCounts] = useState({}); // Track counts for sub-items
  const [showSummary, setShowSummary] = useState(false); // Track summary display

  const handleItemClick = (item) => {
    setSelectedItem(item);
    setShowSummary(false); // Hide summary when selecting a new item
  };

  const incrementSubItemCount = (subItemId) => {
    setSubItemCounts((prevCounts) => ({
      ...prevCounts,
      [subItemId]: (prevCounts[subItemId] || 0) + 1,
    }));
  };

  const decrementSubItemCount = (subItemId) => {
    setSubItemCounts((prevCounts) => ({
      ...prevCounts,
      [subItemId]: Math.max(0, (prevCounts[subItemId] || 0) - 1),
    }));
  };

  const calculateTotal = () => {
    return selectedItem.subItems.reduce((total, subItem) => {
      const count = subItemCounts[subItem.id] || 0;
      return total + count * subItem.price;
    }, 0);
  };

  const handleOrder = () => {
    setShowSummary(true);
  };

  const generateOrderID = () => `ORD-${Math.floor(1000 + Math.random() * 9000)}`;

  return (
    <section id="menu">
      <h2>Our Menu</h2>
      <div className="menu-grid">
        {menuItems.map((item) => (
          <div 
            key={item.id} 
            className="menu-item" 
            onClick={() => handleItemClick(item)}
            style={{ cursor: 'pointer' }}
          >
            <img src={item.image} alt={item.name} />
            <div>
              <h3>{item.name}</h3>
              <p>{item.description}</p>
            </div>
          </div>
        ))}
      </div>

      {/* Show Sub-items if a category is selected */}
      {selectedItem && selectedItem.subItems.length > 0 && (
        <div className="sub-items">
          <h3>{selectedItem.name} Options</h3>
          {selectedItem.subItems.map((subItem) => (
            <div key={subItem.id} className="sub-item">
              <span>{subItem.name}</span>
              <div className="counter-controls">
                <button onClick={() => decrementSubItemCount(subItem.id)}>-</button>
                <span>{subItemCounts[subItem.id] || 0}</span>
                <button onClick={() => incrementSubItemCount(subItem.id)}>+</button>
                <span>₹{subItem.price}</span>
              </div>
            </div>
          ))}
          <div className="total">
            <h4>Total: ₹{calculateTotal()}</h4>
            {calculateTotal() > 0 && (
              <button onClick={handleOrder}>Order</button>
            )}
          </div>
        </div>
      )}

      {/* Order Summary */}
      {showSummary && (
        <div className="order-summary">
          <h3>Order Summary</h3>
          <p>Order ID: {generateOrderID()}</p>
          <ul>
            {selectedItem.subItems.map((subItem) => (
              subItemCounts[subItem.id] > 0 && (
                <li key={subItem.id}>
                  {subItem.name} x {subItemCounts[subItem.id]} = ₹{subItemCounts[subItem.id] * subItem.price}
                </li>
              )
            ))}
          </ul>
          <h4>Grand Total: ₹{calculateTotal()}</h4>
        </div>
      )}
    </section>
  );
};

export default Menu;
