// OwnerDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './OwnerDashboard.css';

function OwnerDashboard() {
  const [restaurantName, setRestaurantName] = useState('');
  const [foodItems, setFoodItems] = useState([]);
  const [newItem, setNewItem] = useState({
    itemName: '',
    description: '',
    price: '',
    imageUrl: ''
  });
  const [message, setMessage] = useState('');
  const [orders, setOrders] = useState([]); // Ensure orders is initialized as an empty array

  useEffect(() => {
    const name = localStorage.getItem('restaurantName');
    setRestaurantName(name || 'Your Restaurant');
  }, []);

  useEffect(() => {
    const fetchFoodItems = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/food-items/${restaurantName}`);
        setFoodItems(response.data);
      } catch (error) {
        console.error("Error fetching food items:", error);
      }
    };
    
    const fetchOrders = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/owner/orders/${restaurantName}`);
        console.log("Fetched Orders:", response.data); // Log the fetched orders
        setOrders(response.data || []); // Use empty array as fallback
      } catch (error) {
        console.error("Error fetching orders:", error);
      }
    };
    
    if (restaurantName) {
      fetchFoodItems();
      fetchOrders();
    }
  }, [restaurantName]);

  const handleAddFoodItem = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        'http://localhost:5000/owner/add-food',
        {
          restaurantName,
          itemName: newItem.itemName,
          description: newItem.description,
          cost: newItem.price,
          imageUrl: newItem.imageUrl
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        }
      );
      setMessage(response.data.message);
      setFoodItems([...foodItems, response.data.foodItem]);
      setNewItem({ itemName: '', description: '', price: '', imageUrl: '' });
    } catch (error) {
      setMessage(error.response?.data?.message || 'Error adding food item');
      console.error("Error adding food item:", error.response?.data || error.message);
    }
  };

  const handleDeleteFoodItem = async (itemId) => {
    console.log("Deleting food item with ID:", itemId);
    try {
      await axios.delete(`http://localhost:5000/owner/delete-food/${itemId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setFoodItems(foodItems.filter(item => item._id !== itemId));
      setMessage("Food item deleted successfully");
    } catch (error) {
      setMessage("Error deleting food item");
      console.error("Error deleting food item:", error.response?.data || error.message);
    }
  };

 

  const handleCompleteOrder = async (orderId) => {
    try {
      const response = await axios.put(`http://localhost:5000/owner/complete-order/${orderId}`);
      
      // Update the orders state by removing the completed order
      setOrders((prevOrders) => prevOrders.filter(order => order._id !== orderId));
  
      alert(response.data.message); // Display success message
    } catch (error) {
      console.error("Error completing order:", error);
      alert("There was an error completing the order.");
    }
  };
  

  return (
    <div className="owner-dashboard-container">
      <h1 className="HOnes">{restaurantName}</h1>
      <h2 className="HTwos">Add Package</h2>
      <form onSubmit={handleAddFoodItem} className="owner-dashboard-form">
        <div>
          <label>Package Name:</label>
          <input
            type="text"
            value={newItem.itemName}
            onChange={(e) => setNewItem({ ...newItem, itemName: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Description:</label>
          <textarea
          value={newItem.description}
          onChange={(e) => setNewItem({ ...newItem, description: e.target.value })}
          required
          rows="4" // Adjust rows as needed for height
          cols="50" // Adjust cols as needed for width
          />
        </div>
        <div>
          <label>Price:</label>
          <input
            type="number"
            value={newItem.price}
            onChange={(e) => setNewItem({ ...newItem, price: e.target.value })}
            required
          />
        </div>
        <div>
          <label>Image URL:</label>
          <input
            type="text"
            value={newItem.imageUrl}
            onChange={(e) => setNewItem({ ...newItem, imageUrl: e.target.value })}
          />
        </div>
        <button type="submit">Add Package</button>
      </form>
      {message && <p className="message">{message}</p>}
      
      <h3 className="HThrees">Current Packages</h3>
      <ul className="food-item-list">
        {foodItems.map((item) => (
          <li key={item._id}>
            {item.imageUrl && <img src={item.imageUrl} alt={item.itemName} />}
            <span>{item.itemName} - ${item.cost}</span>
            <button onClick={() => handleDeleteFoodItem(item._id)}>Delete</button>
          </li>
        ))}
      </ul>
      
      <h3 className="HThrees">Bookings</h3>
      {orders.length > 0 ? (
  <ul className="order-list">
    {orders.map((order) => (
      <li key={order._id}>
        <h4>Booking ID: {order._id}</h4>
        <p>User: {order.userEmail.split('@')[0]}</p>
        <p>Items: </p>
        <ul>
          {Array.isArray(order.items) && order.items.length > 0 ? (
            order.items.map((item) => (
              <li key={item.id || item.itemName}> {/* Use item's unique id if available */}
                {item.itemName} - Quantity: {item.quantity}
              </li>
            ))
          ) : (
            <p>No bookings in this package.</p>
          )}
        </ul>
        <button onClick={() => handleCompleteOrder(order._id)}>Finish the trip</button>
      </li>
    ))}
  </ul>
) : (
  <p>No current orders.</p>
)}

    </div>
  );
}

export default OwnerDashboard;
