import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AgencyDetails from './AgencyDetails';

const Agencies = () => {
  const [agencies, setAgencies] = useState([]);
  const [selectedAgency, setSelectedAgency] = useState(null);

  useEffect(() => {
    const fetchAgencies = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/agencies');
        setAgencies(response.data);
      } catch (error) {
        console.error('Error fetching agencies:', error);
      }
    };
    fetchAgencies();
  }, []);
  
  // This function will be passed to the child component
  const handleBackClick = () => {
    setSelectedAgency(null);
  };

  return (
    <section className="restaurant-list">
      {selectedAgency ? (
        // Pass the agencyName and the new function as props
        <AgencyDetails agencyName={selectedAgency} onBackClick={handleBackClick} />
      ) : (
        <div>
          <h2>Our Partner Agencies</h2>
          {agencies.map((agency) => ( // Use agency._id or agency.agencyName for key
            <div 
              key={agency.agencyName} 
              onClick={() => setSelectedAgency(agency.agencyName)}
              style={{ cursor: 'pointer', marginBottom: '20px', textAlign: 'center' }}
            >
              <img 
                src={agency.agencyImage || 'https://via.placeholder.com/800x500'}
                alt={agency.agencyName} 
                width="800" 
                height="500" 
                style={{ borderRadius: '8px' }} 
              />
              <h3>{agency.agencyName}</h3>
            </div>
          ))}
        </div>
      )}
    </section>
  );
};

export default Agencies;
