import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './RestaurantDetails.css'; // Make sure to import the CSS file

const RestaurantDetails = ({ restaurantName }) => {
    const [foodItems, setFoodItems] = useState([]);
    const [orderMessage, setOrderMessage] = useState(null);

    useEffect(() => {
        const fetchFoodItems = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/food-items/${restaurantName}`);
                setFoodItems(response.data);
            } catch (error) {
                console.error('Error fetching food items:', error);
            }
        };
        fetchFoodItems();
    }, [restaurantName]);

    const handleOrder = async (orderData) => {
        const token = localStorage.getItem('token');

        try {
            const response = await axios.post('http://localhost:5000/order-food', { ...orderData, userEmail: localStorage.getItem('email') }, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });
            setOrderMessage('Booked successfully!');
            console.log('Booked successfully:', response.data);
        } catch (error) {
            setOrderMessage('Error while booking. Please try again.');
            console.error('Error while booking:', error);
        }
    };

    return (
        <div className="RestaurantDetails-container">
            <h2>{restaurantName}</h2>
            {orderMessage && <p className={`order-message ${orderMessage.includes('Error') ? 'error' : ''}`}>{orderMessage}</p>}
            {foodItems.length > 0 ? (
                <ul className="food-items-list">
                    {foodItems.map((item) => (
                        <li key={item._id} className="food-item-card">
                            <img
                                src={item.imageUrl || 'https://via.placeholder.com/100'}
                                alt={item.itemName}
                                width="400"
                                height="400"
                            />
                            <h3>{item.itemName}</h3>
                            <p>{item.description}</p>
                            <p className="cost">Cost:{item.cost}/-</p>
                            <button onClick={() => handleOrder(item)}>Book</button>
                        </li>
                    ))}
                </ul>
            ) : (
                <p className="no-items-message">No packages from this agency.</p>
            )}
        </div>
    );
};

export default RestaurantDetails;
