import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AgencyDetails from './AgencyDetails'; // Renamed component

const Agencies = () => {
  const [agencies, setAgencies] = useState([]); // Renamed state
  const [selectedAgency, setSelectedAgency] = useState(null); // Renamed state

  useEffect(() => {
    const fetchAgencies = async () => {
      try {
        // Updated API endpoint
        const response = await axios.get('http://localhost:5000/api/agencies');
        setAgencies(response.data);
      } catch (error) {
        console.error('Error fetching agencies:', error);
      }
    };
    fetchAgencies();
  }, []);

  return (
    <section className="restaurant-list">
      {selectedAgency ? (
        <AgencyDetails agencyName={selectedAgency} /> // Pass agencyName prop
      ) : (
        <div>
          <h2>Our Partner Agencies</h2>
          {agencies.map((agency, index) => (
            <div 
              key={index} 
              onClick={() => setSelectedAgency(agency.agencyName)} // Use agencyName
              style={{ cursor: 'pointer', marginBottom: '20px', textAlign: 'center' }}
            >
              <img 
                src={agency.agencyImage || 'https://via.placeholder.com/800x500'} // Use agencyImage
                alt={agency.agencyName} 
                width="800" 
                height="500" 
                style={{ borderRadius: '8px' }} 
              />
              <h3>{agency.agencyName}</h3>
            </div>
          ))}
        </div>
      )}
    </section>
  );
};

export default Agencies;
