import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AgencyDashboard.css';
import { useNavigate } from 'react-router-dom';

function AgencyDashboard() {
  const [agencyName, setAgencyName] = useState('');
  const [packages, setPackages] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [newItem, setNewItem] = useState({
    packageName: '',
    description: '',
    price: '',
    imageUrl: ''
  });
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(true); // 1. Add loading state

  // Effect to get agencyName from localStorage
  useEffect(() => {
    const name = localStorage.getItem('agencyName');
    if (name) {
      setAgencyName(name);
    } else {
      setIsLoading(false); // No agency found, stop loading
    }
  }, []);

  // Effect to fetch data once agencyName is set
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!agencyName) return;

    const fetchData = async () => {
      setIsLoading(true);
      try {
        // 2. THIS IS THE CORRECTED API ENDPOINT
        const packagesResponse = await axios.get(`http://localhost:5000/api/agencies/packages/${agencyName}`);
        setPackages(packagesResponse.data);

        const bookingsResponse = await axios.get(`http://localhost:5000/api/agencies/bookings`, {
            headers: { Authorization: `Bearer ${token}` }
        });
        setBookings(bookingsResponse.data || []);

      } catch (error) {
        console.error("Error fetching data:", error);
        setMessage("Could not fetch agency data. Please try again later.");
      } finally {
        setIsLoading(false); // 3. Stop loading after fetch is complete
      }
    };
    
    fetchData();
  }, [agencyName]);

  const handleAddPackage = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(
        'http://localhost:5000/api/agencies/add-package',
        { ...newItem },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      setMessage(response.data.message);
      setPackages([...packages, response.data.tourPackage]);
      setNewItem({ packageName: '', description: '', price: '', imageUrl: '' });
    } catch (error) {
      setMessage(error.response?.data?.message || 'Error adding package');
    }
  };

  const handleDeletePackage = async (packageId) => {
    try {
      await axios.delete(`http://localhost:5000/api/agencies/package/${packageId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setPackages(packages.filter(pkg => pkg._id !== packageId));
      setMessage("Package deleted successfully");
    } catch (error) {
      setMessage("Error deleting package");
    }
  };

  const handleUpdateBooking = async (bookingId) => {
    try {
      const response = await axios.put(`http://localhost:5000/api/agencies/booking/${bookingId}`, 
        { status: 'completed' },
        { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
      );
      setBookings(prevBookings => prevBookings.filter(booking => booking._id !== bookingId));
      alert(response.data.message);
    } catch (error) {
      alert("There was an error updating the booking.");
    }
  };

  // 4. Show a loading message while waiting for data
  if (isLoading) {
    return <div style={{ textAlign: 'center', marginTop: '50px' }}><h2>Loading Dashboard...</h2></div>;
  }
  
  return (
    <div className="owner-dashboard-container">
      <h1 className="HOnes">{agencyName}</h1>
      <h2 className="HTwos">Add New Tour Package</h2>
      <form onSubmit={handleAddPackage} className="owner-dashboard-form">
        <div>
          <label>Package Name:</label>
          <input type="text" value={newItem.packageName} onChange={(e) => setNewItem({ ...newItem, packageName: e.target.value })} required />
        </div>
        <div>
          <label>Description:</label>
          <textarea value={newItem.description} onChange={(e) => setNewItem({ ...newItem, description: e.target.value })} required rows="4" />
        </div>
        <div>
          <label>Price:</label>
          <input type="number" value={newItem.price} onChange={(e) => setNewItem({ ...newItem, price: e.target.value })} required />
        </div>
        <div>
          <label>Image URL:</label>
          <input type="text" value={newItem.imageUrl} onChange={(e) => setNewItem({ ...newItem, imageUrl: e.target.value })} />
        </div>
        <button type="submit">Add Package</button>
      </form>
      {message && <p className="message">{message}</p>}
      
      <h3 className="HThrees">Current Packages</h3>
      {packages.length > 0 ? (
        <ul className="food-item-list">
          {packages.map((pkg) => (
            <li key={pkg._id}>
              {pkg.imageUrl && <img src={pkg.imageUrl} alt={pkg.packageName} />}
              <span>{pkg.packageName} - â‚¹{pkg.price}/-</span>
              <button onClick={() => handleDeletePackage(pkg._id)}>Delete</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No packages have been added yet.</p>
      )}
      
      <h3 className="HThrees">Active Bookings</h3>
      {bookings.length > 0 ? (
        <ul className="order-list">
          {bookings.map((booking) => (
            <li key={booking._id}>
              <h4>Booking for: {booking.packageName}</h4>
              <p>User: {booking.userEmail}</p>
              <p>Status: {booking.status}</p>
              <button onClick={() => handleUpdateBooking(booking._id)}>Mark as Completed</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>No current bookings.</p>
      )}
    </div>
  );
}

export default AgencyDashboard;
